import type { ColumnType } from "kysely";

export type Generated<T> = T extends ColumnType<infer S, infer I, infer U>
  ? ColumnType<S, I | undefined, U>
  : ColumnType<T, T | undefined, T>;

export type Int8 = ColumnType<string, string | number | bigint, string | number | bigint>;

export interface Admin {
  id: Generated<number>;
  email: string;
  password: string;
}

export interface PgStatStatements {
  userid: number | null;
  dbid: number | null;
  queryid: Int8 | null;
  query: string | null;
  calls: Int8 | null;
  total_time: number | null;
  min_time: number | null;
  max_time: number | null;
  mean_time: number | null;
  stddev_time: number | null;
  rows: Int8 | null;
  shared_blks_hit: Int8 | null;
  shared_blks_read: Int8 | null;
  shared_blks_dirtied: Int8 | null;
  shared_blks_written: Int8 | null;
  local_blks_hit: Int8 | null;
  local_blks_read: Int8 | null;
  local_blks_dirtied: Int8 | null;
  local_blks_written: Int8 | null;
  temp_blks_read: Int8 | null;
  temp_blks_written: Int8 | null;
  blk_read_time: number | null;
  blk_write_time: number | null;
}

export interface Students {
  id: Generated<number>;
  first_name: string;
  last_name: string | null;
  age: number;
  gender: string;
  teacher_id: number;
}

export interface Teachers {
  id: Generated<number>;
  first_name: string;
  last_name: string | null;
  age: number;
  gender: string;
  email: string | null;
}

export interface DB {
  admin: Admin;
  pg_stat_statements: PgStatStatements;
  students: Students;
  teachers: Teachers;
}
